example.code:

class OpendirDemo {
  public static void Main(string[] argv)
	{
		IntPtr dir = Syscall.opendir("/tmp");
		Dirent nextentry;

		while ((nextentry = Syscall.readdir (dir)) != null) {
			Console.WriteLine(nextentry.d_name);
		}

		Syscall.closedir (dir);
	}
}


class ReaddirDemo {
	public static void Main(string[] argv)
	{
		IntPtr dir = Syscall.opendir("/tmp");
		Dirent nextentry;

		while ((nextentry = Syscall.readdir (dir)) != null) {
			Console.WriteLine(nextentry.d_name);
		}

		Syscall.closedir (dir);
	}
}
