			Assert.IsNotNull (mreOrig, "#B1-1");
			Assert.AreEqual (mreOrig.Buffer, mre.Buffer, "#B1-2");
			Assert.AreEqual (mreOrig.Length, mre.Length, "#B1-3");

			using (ms = new MemoryStream (fileResponseElement))
