Console.WriteLine(uri.AbsoluteUri);
Console.WriteLine(uri.PathAndQuery);

Uri uri = new Uri("ftp://myUrl/%2E%2E/%2E%2E");
Console.WriteLine(uri.AbsoluteUri);
Console.WriteLine(uri.PathAndQuery);

