example.code:
using System;
public class Ints {
   public int[] myInts;
}
public class NullRefExample {
   public static void Main() {
      Ints ints = new Ints();
      try {
         int i = ints.myInts[0];
      }
      catch( NullReferenceException e ) {
         Console.WriteLine( "Caught error: {0}.", e);
      }
   }
}
