example.code:
using System;
using System.IO;

public class ObjectDisposedExceptionTest {
   public static void Main() {     
      MemoryStream ms = new MemoryStream(16);
      ms.Close();
      try {
         ms.ReadByte();
      } 
      catch (ObjectDisposedException e) {
         Console.WriteLine("Caught: {0}", e.Message);
      }
   }
}

using System;

public class ExampleDisposableObject : IDisposable {
 public static void Main() {

 ExampleDisposableObject obj = new ExampleDisposableObject();

 obj.Close();

 try {
 Console.WriteLine(obj);
 } catch (ObjectDisposedException e) {
 Console.WriteLine("Caught: {0}", e.Message);
 }
 }


 public ExampleDisposableObject() {
 isDisposed = false;
 }

 ~ExampleDisposableObject() {
 Dispose(true);
 }

 public void Close() {
 Dispose(true);
 }

 public void Dispose() {
 Dispose(true);
 }

 public void Dispose(bool disposing) {
 isDisposed = true;
 }

 public override String ToString() {
 if(isDisposed)
 throw new ObjectDisposedException("ExampleDisposableObject");
 else
 return "This is an instance of ExampleDisposableObject.";
 }

 private bool isDisposed;
}

using System;

public class ExampleDisposableObject : IDisposable {
 public static void Main() {

 ExampleDisposableObject obj = new ExampleDisposableObject();

 obj.Close();

 try {
 Console.WriteLine(obj);
 } catch (ObjectDisposedException e) {
 Console.WriteLine("Caught: {0}", e.Message);
 }
 }


 public ExampleDisposableObject() {
 isDisposed = false;
 }

 ~ExampleDisposableObject() {
 Dispose(true);
 }

 public void Close() {
 Dispose(true);
 }

 public void Dispose() {
 Dispose(true);
 }

 public void Dispose(bool disposing) {
 isDisposed = true;
 }

 public override String ToString() {
 if(isDisposed) {
 string message = "Oh-oh! This object has been disposed!";
 string objectName = "ExampleDisposableObject"; 
 throw new ObjectDisposedException(objectName, message);
 }
 else
 return "Hello, World!";
 }

 private bool isDisposed;
}
